    private void CBKodeMActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CBKodeMActionPerformed
        // TODO add your handling code here:
        String kodemobil,Merekmobil;
        double hargamobil;
        
        kodemobil = String.valueOf(CBKodeM.getSelectedItem());
       
        if (kodemobil.equals("A001")) {
        Merekmobil="Honda";
        hargamobil= 100000;
        }
        else if (kodemobil.equals("B002")) {
        Merekmobil="Toyota";
        hargamobil=150000;
        }
        else if (kodemobil.equals("C003")) {
        Merekmobil="Suzuki";
        hargamobil=200000;
        }
        else if (kodemobil.equals("D004")) {
        Merekmobil="BMW";
        hargamobil=100000;
        }
        else {
        Merekmobil="Data Tidak Ditemukan";
        hargamobil=0;
        }
         
        MM.setText(""+Merekmobil);
        HM.setText(""+hargamobil);
    }//GEN-LAST:event_CBKodeMActionPerformed

    private void btnHitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHitungActionPerformed
        // TODO add your handling code here:
        double jmlhbeli;
        double result;
        double harga;
        
        jmlhbeli = Double.parseDouble(JB.getText());
        harga = Double.parseDouble(PH.getText());
        
        result = jmlhbeli * harga ;
        
        TB.setText(""+result);
        
    }//GEN-LAST:event_btnHitungActionPerformed

