    private void PHActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PHActionPerformed
        // TODO add your handling code here:
        double hargamobil, jmlbeli, totalbayar, potongan;
        
        hargamobil = Double.parseDouble(HM.getText());
        jmlbeli = Double.parseDouble(JB.getText());
        potongan = Double.parseDouble(PH.getText());
        
        totalbayar = (hargamobil * jmlbeli)-potongan;
        
        TB.setText(""+totalbayar);
    }//GEN-LAST:event_PHActionPerformed

    private void btnHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHapusActionPerformed
        // TODO add your handling code here:
        HM.setText(null);
        MM.setText(null);
        JB.setText(null);
        TB.setText(null);
        JU.setText(null);
        KEM.setText(null);
        PH.setText(null);
        CBKodeM.setSelectedIndex(0);
    }//GEN-LAST:event_btnHapusActionPerformed

    private void JUKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_JUKeyPressed
        // TODO add your handling code here:
        double totalbayar, uang, kembalian;
        
        totalbayar = Double.parseDouble(TB.getText());
        uang = Double.parseDouble(JU.getText());
        
        if(uang < totalbayar) {
            KEM.setText("UANG ANDA KURANG ESS !!");
        } else {
            kembalian = uang - totalbayar;
            KEM.setText(""+kembalian);
        }
    }//GEN-LAST:event_JUKeyPressed

